void GPIOSelect(uint32_t stage){
	//select stage.
	switch (stage){
		case 1:
			GPIO_1->ODR &= ~(1 << GPIO_1_num);
			GPIO_2->ODR |= (1 << GPIO_2_num);
			GPIO_3->ODR |= (1 << GPIO_3_num);
			GPIO_4->ODR |= (1 << GPIO_4_num);
			break;
		case 2:
			GPIO_1->ODR |= (1 << GPIO_1_num);
			GPIO_2->ODR &= ~(1 << GPIO_2_num);
			GPIO_3->ODR |= (1 << GPIO_3_num);
			GPIO_4->ODR |= (1 << GPIO_4_num);
			break;
		case 3:
			GPIO_1->ODR |= (1 << GPIO_1_num);
			GPIO_2->ODR |= (1 << GPIO_2_num);
			GPIO_3->ODR &= ~(1 << GPIO_3_num);
			GPIO_4->ODR |= (1 << GPIO_4_num);
			break;
		case 4:
			GPIO_1->ODR |= (1 << GPIO_1_num);
			GPIO_2->ODR |= (1 << GPIO_2_num);
			GPIO_3->ODR |= (1 << GPIO_3_num);
			GPIO_4->ODR &= ~(1 << GPIO_4_num);
			break;
		default:
			break;
	}
	return;
}

